import React, { useEffect, useState } from "react";
import { auth, db } from '../../firebase';
import {
  collection,
  addDoc,
  deleteDoc,
  doc,
  onSnapshot,
  orderBy,
  query,
  Timestamp,
  where,
} from "firebase/firestore";
import "./PantryPage.css"

export default function PantryPage() {
    const [name, setName] = useState("");
    const [expiry, setExpiry] = useState("");
    const [items, setItems] = useState<any[]>([]);

//Get user's pantry items from Firestore
    useEffect(() => {
        if (!auth.currentUser)
            return;

        const dbsearch = query(
            collection(db, "pantry"),
            where("userId", "==", auth.currentUser.uid),
            orderBy("expiryDate")
        );

        const unsubscribe = onSnapshot(dbsearch, (snapshot) => {
            const data = snapshot.docs.map((d) => ({
                id: d.id,
                ...d.data(),
            }));
            setItems(data as any[]);
        });

        return () => unsubscribe();
    }, []);

    //Add item into Firestore
    const handleAdd = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!auth.currentUser || name.trim() === "" || expiry === "")
            return;
        const expiryDate = Timestamp.fromDate(new Date(expiry));

    try {
        await addDoc(collection(db, "pantry"), {
            userId: auth.currentUser.uid,
            name: name.trim(),
            expiryDate,
        });

        setName("");
        setExpiry("");
    } catch (err) {
        console.error("Error: ", err);
    }
};

const handleRemove = async(id: string) => {
    try {
        await deleteDoc(doc(db, "pantry", id));
    } catch (err) {
        console.error("Error: ", err);
    }
};

    return (
        <div className = "pantryOverview">
            <h2 className = "pantryHeading">Pantry</h2>
            <form onSubmit = {handleAdd} className = "pantryForm">
                <input 
                 type = "text"
                 placeholder = "Food Name"
                 value = {name}
                 onChange = {(e) => setName(e.target.value)}
                />
                <input
                 type = "date"
                 value = {expiry}
                 onChange = {(e) => setExpiry(e.target.value)}
                />
                <button type = "submit">Add</button>
            </form>
 {/* Show all the user’s items (with Delete buttons) */}
      {items.length === 0 && <p>(No items in your pantry yet.)</p>}
      <ul>
        {items.map((item) => {
          const expiryString = item.expiryDate
            .toDate()
            .toDateString();
          return (
            <li key={item.id} style={{ marginBottom: 8 }}>
              {item.name} — Expires on {expiryString}{" "}
              <button onClick={() => handleRemove(item.id)}>
                Delete
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}
