import React, { useState, useEffect } from 'react';
import './CSS/App.css';
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { auth } from "./firebase";
import { User, onAuthStateChanged, signOut } from "firebase/auth";
import UserSignUp from "./CSS/Authentication/signup";
import UserLogin from "./CSS/Authentication/login"; 
import Content from "./Content";

function App() {
  const [user, setUser] = useState<User | null>(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return unsubscribe;
  }, []);

  const handleLogout = () => {
    signOut(auth);
  };

  return (
    <Router>
      <header className = "authen">
        <h1 className ='title'>Shelf Aware</h1>
        <nav>
          {user ? (
            <>
             <span className = "welcomeText">
               WelcomeðŸ‘‹, {user.displayName || user.email}
             </span>
             <button className = "logoutbutton" onClick = {handleLogout}>
               Logout
             </button>
            </>
          ) : (
            <>
              <Link to = "/signup" className = "loginbutton"> Sign Up </Link>
              <Link to = "/login" className = "loginbutton"> Login </Link>
            </>
          )}
        </nav>
      </header>

     <main className = "App">
       <Routes>
          <Route path="/" element={<Content />} />
          <Route path = "/signup" element = {<UserSignUp />} />
          <Route path = "/login" element = {<UserLogin />} />
       </Routes>
     </main>
    </Router>
  );
}

export default App;
