import React, { useEffect, useState } from "react";
import { auth, db } from '../../firebase';
import {
  collection,
  addDoc,
  deleteDoc,
  doc,
  onSnapshot,
  orderBy,
  query,
  Timestamp,
  where,
} from "firebase/firestore";
import "./PantryPage.css"

interface PantryItem {
    id: string;
    userId: string;
    name: string;
    expiryDate: { toDate: () => Date};
}

export default function PantryPage() {
    const [items, setItems] = useState<PantryItem[]>([]);
    const [name, setName] = useState("");
    const [expiry, setExpiry] = useState("");

//Get user's pantry items from Firestore
    useEffect(() => {
        if (!auth.currentUser)
            return;

        const dbsearch = query(
            collection(db, "pantry"),
            where("userId", "==", auth.currentUser.uid),
            orderBy("expiryDate")
        );

        const unsubscribe = onSnapshot(dbsearch, (snapshot) => {
            const data: PantryItem[] = snapshot.docs.map((d) => ({
                id: d.id,
                ...(d.data() as Omit<PantryItem, "id">),
            }));
            setItems(data);
        });

        return () => unsubscribe();
    }, []);

//Add item into Firestore
    const handleAdd = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!auth.currentUser || name.trim() === "" || expiry === "")
            return;
        const expiryDate = Timestamp.fromDate(new Date(expiry));

    try {
        await addDoc(collection(db, "pantry"), {
            userId: auth.currentUser.uid,
            name: name.trim(),
            expiryDate,
        });

        setName("");
        setExpiry("");
    } catch (err) {
        console.error("Error: ", err);
    }
};


const handleRemove = async(id: string) => {
    try {
        await deleteDoc(doc(db, "pantry", id));
    } catch (err) {
        console.error("Error: ", err);
    }
};

    return (
        <div className = "pantryOverview">
            <div className = "pantryHeading">
                <h1 className = "pantryListTitle">Inventory</h1>
            </div>
                {items.length === 0 ? (<p className = "noItemText">(No items in pantry yetðŸ˜”)</p>
                ) : (
                    <div className = "pantryList">
                        {items.map((item) => {
                            const expiryDateObj = item.expiryDate.toDate();
                            const expiryDate = expiryDateObj.toDateString();
                            const daysLeft = Math.floor(
                                (expiryDateObj.getTime() - new Date().getTime()) / (1000 * 60 * 60 * 24)
                            );

                let expiryClass = "expiry-later";
                if (daysLeft <= 7) expiryClass = "expiry-soon";
                else if (daysLeft <= 30) expiryClass = "expiry-medium";

                return (
                    <div key={item.id} className="pantryMove">
                        <button
                            className="pantryDelete"
                            onClick={() => handleRemove(item.id)}
                        >
                            Ã—
                        </button>
                        <div className="pantryBody">
                        <p className="pantryName">{item.name}</p>
                        <p className={`pantryExpiry ${expiryClass}`}>
                            Expires on {expiryDate}
                        </p>
                    </div>
                </div>
            );
        })}
    </div>
  )}

    <button className = "pantryAddBtn" onClick = {handleAdd}>Add Item</button>
        <form onSubmit = {handleAdd} className = "pantryForm">
            <input 
                type = "text"
                placeholder = "Food Name"
                value = {name}
                onChange = {(e) => setName(e.target.value)}
            />
            <input
                type = "date"
                value = {expiry}
                onChange = {(e) => setExpiry(e.target.value)}
            />
            <button type = "submit">Add</button>
        </form>
    </div>
  );
}
